public class BreakandContinue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		for( int i=1;i<=10;i++)
		{
			System.out.println(i +"Hello");
			 if(i>3 & i==7)
			 {
				// break;
				 continue;
			 }
			
			System.out.println(i +"java");
			
		}
	}

}
