package day4;

public class SwapDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a=10;
		int b=20;
		
		int temp =a;
		a=b;
		b=temp;
		System.out.println("After swap");
		System.out.println("value of a" +a);
		System.out.println("value of b" +b);
	}

}
